import { Jsonnet } from '@hanazuki/node-jsonnet';
import * as path from 'path';
import { OpenAiEndpoint } from '../lib/OpenAiEndpoint';
import { PostgresClient } from '../lib/PostgresClient';
import { ArkRequest } from '../types/ArkRequest';
enum PostgresDistanceMetric {
    COSINE = 'COSINE',
    IP = 'IP',
    L2 = 'L2',
}

const gpt3endpoint = new OpenAiEndpoint(
    'https://api.openai.com/v1/chat/completions',
    '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',
    '',
    'gpt-3.5-turbo',
    'user',
    0.7
);

export async function hydeSearchAdaEmbedding(arkRequest: ArkRequest) {
    try {
        // Get required params from API...
        const table = 'ada_hyde_prod';
        const namespace = '360_docs';
        const query = arkRequest.query;
        const topK = Number(arkRequest.topK);

        //
        const jsonnet = new Jsonnet();

        const promptPath = path.join(process.cwd(), './src/hydeExample/prompts.jsonnet');
        const hydePath = path.join(process.cwd(), './src/hydeExample/hyde.jsonnet');
        // Load Jsonnet to extract args..
        const promptLoader = await jsonnet.evaluateFile(promptPath);

        // Getting ${summary} basePrompt
        const promptTemplate = JSON.parse(promptLoader).summary;

        // Getting the updated promptTemplate with query
        let hydeLoader = await jsonnet
            .extString('promptTemplate', promptTemplate)
            .extString('time', '')
            .extString('query', query)
            .evaluateFile(hydePath);

        // Get concatenated prompt
        const prompt = JSON.parse(hydeLoader).prompt;

        // Block and get the response from GPT3
        const gptResponse = await gpt3endpoint.gptFn(prompt);

        // Chain 1 ==> Get Gpt3Response & split
        const gpt3Responses = gptResponse.split('\n');

        // Chain 2 ==> Get Embeddings from OpenAI using Each Response
        const embeddingsListChain: Promise<number[][]> = Promise.all(
            gpt3Responses.map(async (resp) => {
                const embedding = await gpt3endpoint.embeddings(resp);
                return embedding;
            })
        );

        // Chain 5 ==> Query via EmbeddingChain
        const dbClient = new PostgresClient(
            await embeddingsListChain,
            PostgresDistanceMetric.IP,
            topK,
            20,
            table,
            namespace,
            arkRequest,
            15
        );
        const queryResult = await dbClient.dbQuery();

        // Chain 6 ==> Create Prompt using Embeddings
        const retrievedDocs: string[] = [];

        for (const embeddings of queryResult) {
            retrievedDocs.push(
                `${embeddings.raw_text}\n score:${embeddings.score}\n filename:${embeddings.filename}\n`
            );
        }

        if (retrievedDocs.join('').length > 4096) {
            retrievedDocs.length = 4096;
        }

        const currentTime = new Date().toLocaleString();
        const formattedTime = currentTime;

        // System prompt
        const ansPromptSystem = JSON.parse(promptLoader).ans_prompt_system;

        hydeLoader = await jsonnet
            .extString(promptTemplate, ansPromptSystem)
            .extString('time', formattedTime)
            .extString('qeury', retrievedDocs.join(''))
            .evaluateFile(hydePath);

        const finalPromptSystem = JSON.parse(hydeLoader).prompt;

        // User prompt
        const ansPromptUser = JSON.parse(promptLoader).ans_prompt_user;

        hydeLoader = await jsonnet
            .extString(promptTemplate, ansPromptUser)
            .extString('qeury', query)
            .evaluateFile(hydePath);
        const finalPromptUser = JSON.parse(hydeLoader).prompt;

        const chatMessages = [
            { role: 'system', content: finalPromptSystem },
            { role: 'user', content: finalPromptUser },
        ];

        const finalAnswer = await gpt3endpoint.gptFnChat(chatMessages);

        const response = {
            wordEmbeddings: queryResult,
            finalAnswer: finalAnswer,
        };

        return response;
    } catch (error) {
        // Handle errors here
        console.error(error);
        throw error;
    }
}

import axios from 'axios';

export class OpenAiEndpoint {
    url: string;
    apiKey: string;
    orgId: string;
    model: string;
    role: string;
    temprature: number;

    constructor(
        url: string,
        apiKey: string,
        orgId: string,
        model: string,
        role: string,
        temprature: number
    ) {
        this.url = url;
        this.apiKey = apiKey;
        this.orgId = orgId;
        this.model = model;
        this.role = role;
        this.temprature = temprature;
    }

    async gptFn(prompt: string): Promise<string> {
        const responce = await axios
            .post(
                'https://api.openai.com/v1/chat/completions',
                {
                    model: this.model,
                    messages: [
                        {
                            role: this.role,
                            content: prompt,
                        },
                    ],
                    temperature: this.temprature,
                },
                {
                    headers: {
                        Authorization: 'Bearer ' + this.apiKey,
                        'content-type': 'application/json',
                    },
                }
            )
            .then(function (response) {
                return response.data.choices;
            })
            .catch(function (error) {
                if (error.response) {
                    console.log('Server responded with status code:', error.response.status);
                    console.log('Response data:', error.response.data);
                } else if (error.request) {
                    console.log('No response received:', error.request);
                } else {
                    console.log('Error creating request:', error.message);
                }
            });
        return responce[0].message.content;
    }

    async embeddings(resp: string): Promise<number[]> {
        const responce = await axios
            .post(
                'https://api.openai.com/v1/embeddings',
                {
                    model: 'text-embedding-ada-002',
                    input: resp,
                },
                {
                    headers: {
                        Authorization: 'Bearer ' + this.apiKey,
                        'content-type': 'application/json',
                    },
                }
            )
            .then(function (response) {
                return response.data.data[0].embedding;
            })
            .catch(function (error) {
                if (error.response) {
                    console.log('Server responded with status code:', error.response.status);
                    console.log('Response data:', error.response.data);
                } else if (error.request) {
                    console.log('No response received:', error.request);
                } else {
                    console.log('Error creating request:', error.message);
                }
            });

        return responce;
    }

    async gptFnChat(chatMessages) {
        const responce = await axios
            .post(
                'https://api.openai.com/v1/chat/completions',
                {
                    model: this.model,
                    messages: chatMessages,
                    temperature: this.temprature,
                },
                {
                    headers: {
                        Authorization: 'Bearer ' + this.apiKey,
                        'content-type': 'application/json',
                    },
                }
            )
            .then(function (response) {
                return response.data.choices;
            })
            .catch(function (error) {
                if (error.response) {
                    console.log('Server responded with status code:', error.response.status);
                    console.log('Response data:', error.response.data);
                } else if (error.request) {
                    console.log('No response received:', error.request);
                } else {
                    console.log('Error creating request:', error.message);
                }
            });

        return responce[0].message.content;
    }

    async gptFnTestGenerator(prompt: string): Promise<string> {
        const responce = await axios
            .post(
                'https://api.openai.com/v1/chat/completions',
                {
                    model: this.model,
                    messages: [
                        {
                            role: this.role,
                            content: prompt,
                        },
                    ],
                    temperature: this.temprature,
                },
                {
                    headers: {
                        Authorization: 'Bearer ' + this.apiKey,
                        'content-type': 'application/json',
                    },
                }
            )
            .then(function (response) {
                return response.data.choices;
            })
            .catch(function (error) {
                if (error.response) {
                    console.log('Server responded with status code:', error.response.status);
                    console.log('Response data:', error.response.data);
                } else if (error.request) {
                    console.log('No response received:', error.request);
                } else {
                    console.log('Error creating request:', error.message);
                }
            });
        return responce[0].message.content;
    }
}

import { createConnection } from 'typeorm';

export class PostgresClient {
    wordEmbeddings: number[][];
    metric: PostgresDistanceMetric;
    topK: number;
    probes: number;
    tableName: string;
    namespace: string;
    arkRequest: any;
    upperLimit: number;

    constructor(
        wordEmbeddings: number[][],
        metric,
        topK,
        probes,
        tableName,
        namespace: string,
        arkRequest: any,
        upperLimit
    ) {
        this.wordEmbeddings = wordEmbeddings;
        this.metric = metric;
        this.topK = topK;
        this.probes = probes;
        this.tableName = tableName;
        this.namespace = namespace;
        this.arkRequest = arkRequest;
        this.upperLimit = upperLimit;
    }

    async dbQuery() {
        const con = await createConnection();
        const entityManager = con.createEntityManager();
        try {
            const query1 = `SET LOCAL ivfflat.probes = ${this.probes};`;
            await entityManager.query(query1);

            let query: string = '';

            for (let i = 0; i < this.wordEmbeddings.length; i++) {
                const embedding: string = JSON.stringify(this.wordEmbeddings[i]);

                query += `( SELECT id, raw_text, document_date, metadata, namespace, filename, timestamp, 
                ${
                    this.arkRequest.textWeight.baseWeight
                } / (ROW_NUMBER() OVER (ORDER BY text_rank DESC) + ${
                    this.arkRequest.textWeight.fineTuneWeight
                }) +
                ${
                    this.arkRequest.similarityWeight.baseWeight
                } / (ROW_NUMBER() OVER (ORDER BY similarity DESC) + ${
                    this.arkRequest.similarityWeight.fineTuneWeight
                }) +
                ${
                    this.arkRequest.dateWeight.baseWeight
                } / (ROW_NUMBER() OVER (ORDER BY date_rank DESC) + ${
                    this.arkRequest.dateWeight.fineTuneWeight
                }) AS rrf_score
                FROM ( SELECT sv.id, sv.raw_text, sv.namespace, sv.filename, sv.timestamp, svtm.document_date, svtm.metadata, ts_rank_cd(sv.tsv, plainto_tsquery('${'english'}', '${
                    this.arkRequest.query
                }')) AS text_rank, `;

                if (this.metric === PostgresDistanceMetric.COSINE)
                    query += `1 - (sv.embedding <=> '${embedding}') AS similarity, `;
                if (this.metric === PostgresDistanceMetric.IP)
                    query += `(sv.embedding <#> '${embedding}') * -1 AS similarity, `;
                if (this.metric === PostgresDistanceMetric.L2)
                    query += `sv.embedding <-> '${embedding}' AS similarity, `;

                query += `CASE WHEN svtm.document_date IS NULL THEN 0 ELSE EXTRACT(YEAR FROM svtm.document_date) * 365 + EXTRACT(DOY FROM svtm.document_date) END AS date_rank FROM (SELECT id, raw_text, embedding, tsv, namespace, filename, timestamp from ${this.tableName} WHERE namespace = '${this.namespace}'`;

                if (this.metric === PostgresDistanceMetric.COSINE)
                    query += ` ORDER BY embedding <=> '${embedding}'  LIMIT ${this.topK}`;
                if (this.metric === PostgresDistanceMetric.IP)
                    query += ` ORDER BY embedding <#> '${embedding}'  LIMIT ${this.topK}`;
                if (this.metric === PostgresDistanceMetric.L2)
                    query += ` ORDER BY embedding <-> '${embedding}'  LIMIT ${this.topK}`;

                query += `) sv JOIN ${this.tableName}_join_${this.arkRequest.metadataTable} jtm ON sv.id = jtm.id JOIN ${this.tableName}_${this.arkRequest.metadataTable} svtm ON jtm.metadata_id = svtm.metadata_id) subquery `;

                switch (this.arkRequest.orderRRF) {
                    case 'text_rank':
                        query += `ORDER BY text_rank DESC, rrf_score DESC`;
                        break;
                    case 'similarity':
                        query += `ORDER BY similarity DESC, rrf_score DESC`;
                        break;
                    case 'date_rank':
                        query += `ORDER BY date_rank DESC, rrf_score DESC`;
                        break;
                    case 'default':
                        query += `ORDER BY rrf_score DESC`;
                        break;
                }

                query += ` LIMIT ${this.topK})`;
                if (i < this.wordEmbeddings.length - 1) {
                    query += ' UNION ALL \n';
                }
            }

            if (this.wordEmbeddings.length > 1) {
                query = `SELECT * FROM (SELECT DISTINCT ON (result.id) * FROM ( ${query} ) result) subquery ORDER BY rrf_score DESC LIMIT ${this.upperLimit};`;
            } else {
                query += ` ORDER BY rrf_score DESC LIMIT ${this.topK};`;
            }
            const results = await entityManager.query(query);
            await con.destroy();
            return results;
        } catch (error) {
            // Handle errors here
            console.error(error);
            throw error;
        }
    }
}

